import React, { useCallback, useState, forwardRef } from "react";
import { Input } from "@chakra-ui/core";
import { keys } from "../../__mocks__";
import { Button } from "../../stories/Button";

export const getRandomKey = (): string => { return keys[Math.floor(Math.random() * keys.length)] };

const SingleInput = forwardRef(({ handleChange, keyValue, placeholder, id }: { handleChange: any, keyValue: string, placeholder: string, id: number }, ref) => {

    return (
        <Input id={id} name="" size="lg" type="text" placeholder={placeholder} w="3rem" value={keyValue} onChange={handleChange} />
    )
});

export const Exercise = () => {
    const [keyValues, setKeyValue] = useState(["", "", "", ""]);
    const [targetsKeyValues, setTargetsKeyValue] = useState([getRandomKey(), getRandomKey(), getRandomKey(), getRandomKey()]);
    const [error, setError] = useState(0);
    const [valid, setValid] = useState(0);
    const handleChange = (index: number) => (onKeyPress: any) => {
        if (onKeyPress.target.value === targetsKeyValues[index]) {
            setKeyValue((previousArray: string[]): string[] => {
                const newArray = [...previousArray];
                newArray[index] = onKeyPress.target.value;
                return newArray;
            });
            setValid(valid + 1);
            getElementById(index + 1) === null ? console.log("the end") : document.getElementById(index + 1).focus();
        } else {
            setError(error + 1);
        }
    }
    const reset = useCallback(() => { setTargetsKeyValue([getRandomKey(), getRandomKey(), getRandomKey(), getRandomKey()]) }, []);
    return (
        <div>
            Try this first Exercise: <br />
            <SingleInput id={0} handleChange={handleChange(0)} keyValue={keyValues[0]} placeholder={targetsKeyValues[0]} />
            <SingleInput id={1} handleChange={handleChange(1)} keyValue={keyValues[1]} placeholder={targetsKeyValues[1]} />
            <SingleInput id={2} handleChange={handleChange(2)} keyValue={keyValues[2]} placeholder={targetsKeyValues[2]} />
            <SingleInput id={3} handleChange={handleChange(3)} keyValue={keyValues[3]} placeholder={targetsKeyValues[3]} />
            <div>{valid} valids | {error} errors</div>
            <Button label={"Go to next exercise"} onClick={reset} >Go to next exercise</Button>
        </div>
    )
}

//change valid, error and key value when reset
