import React, { useCallback, useState, forwardRef, useRef } from "react";
import { Input } from "@chakra-ui/core";
import { keys } from "../../__mocks__";
import { Button } from "../../stories/Button";

const getRandomKey = (): string => { return keys[Math.floor(Math.random() * keys.length)] };

const SingleInput = forwardRef(({ handleChange, keyValue, placeholder }: { handleChange: any, keyValue: string, placeholder: string }, ref: any) => {

    return (
        <Input ref={ref} name="" size="lg" type="text" placeholder={placeholder} w="3rem" value={keyValue} onChange={handleChange} />
    )
});

let onKeyPressCount = 0;
export const Exercise = () => {
    const [keyValues, setKeyValue] = useState(["", "", "", ""]);
    const [targetsKeyValues, setTargetsKeyValue] = useState([getRandomKey(), getRandomKey(), getRandomKey(), getRandomKey()]);

    const [error, setError] = useState(0);
    const [valid, setValid] = useState(0);

    const focusRef0: React.RefObject<HTMLInputElement> = useRef(null);
    const focusRef1: React.RefObject<HTMLInputElement> = useRef(null);
    const focusRef2: React.RefObject<HTMLInputElement> = useRef(null);
    const focusRef3: React.RefObject<HTMLInputElement> = useRef(null);
    const focusRef4: React.RefObject<HTMLInputElement> = useRef(null);
    const handleChange = (index: number) => (onKeyPress: any) => {
        if (onKeyPress.target.value === targetsKeyValues[index]) {
            setKeyValue((previousArray: string[]): string[] => {
                const newArray = [...previousArray];
                newArray[index] = onKeyPress.target.value;
                return newArray;
            });
            setValid(valid + 1);
            onKeyPressCount = onKeyPressCount + 1;
            if (index === 0) {
                focusRef1.current!.focus();
            } else if (index === 1) {
                focusRef2.current!.focus();
            } else if (index === 2) {
                focusRef3.current!.focus();
            } else {
                focusRef4.current!.focus();
            }
        } else {
            setError(error + 1);
            onKeyPressCount = onKeyPressCount + 1;
        }
    }
    console.log("onKeyPressCount", onKeyPressCount);
    console.log("correct", valid);
    console.log("error", error);
    console.log("correct-error", valid - error);
    console.log("rate", (valid - error) / onKeyPressCount);
    const percentage = () => {
        let result = 0;
        const rate = (valid - error) / onKeyPressCount;
        if (rate < 0 || onKeyPressCount === 0) {
            result = 0;
        } else {
            result = rate;
        }
        return result
    }
    console.log("percentage", percentage());
    const reset = useCallback(() => { setTargetsKeyValue([getRandomKey(), getRandomKey(), getRandomKey(), getRandomKey()]) }, []);
    return (
        <div>
            Try this first Exercise: <br />
            <SingleInput ref={focusRef0} handleChange={handleChange(0)} keyValue={keyValues[0]} placeholder={targetsKeyValues[0]} />
            <SingleInput ref={focusRef1} handleChange={handleChange(1)} keyValue={keyValues[1]} placeholder={targetsKeyValues[1]} />
            <SingleInput ref={focusRef2} handleChange={handleChange(2)} keyValue={keyValues[2]} placeholder={targetsKeyValues[2]} />
            <SingleInput ref={focusRef3} handleChange={handleChange(3)} keyValue={keyValues[3]} placeholder={targetsKeyValues[3]} />
            <div ref={focusRef4}>{valid} corrects | {error} errors | {onKeyPressCount === 0 ? 100 : onKeyPressCount * 100 / valid}%</div>
            <Button label={"Go to next exercise"} onClick={reset} >Go to the next exercise</Button>
        </div >
    )
};

//change valid, error and key value when reset
