import React, { useState } from "react";
import { Input } from "@chakra-ui/core";
import { randomKey } from "../../__mocks__";


let letter = randomKey;
export const Exercise = () => {
    const [value, setValue] = useState("");
    const [error, setError] = useState(0);
    const [valid, setValid] = useState(0);
    const handleChange = (onKeyPress: any) => {
        if (onKeyPress.target.value === letter) {
            setValue(onKeyPress.target.value);
            setValid(valid + 1)
        } else { setError(error + 1) }
    }
    return (
        <div>
            Try this first Exercise: <br />
            <Input size="lg" type="text" placeholder={letter} w="3rem" value={value} onChange={handleChange} />
            {/* <Input size="lg" type="text" placeholder={letter} w="3rem" value={value} onChange={handleChange} />
            <Input size="lg" type="text" placeholder={letter} w="3rem" value={value} onChange={handleChange} />
            <Input size="lg" type="text" placeholder={letter} w="3rem" value={value} onChange={handleChange} /> */}
            <div>{valid} valids | {error} errors</div>
        </div>
    )
}
