import React, { useState } from "react";
import { Input } from "@chakra-ui/core";

export const Exercise = () => {
    const letter = "a";
    let score = 0;
    const [value, setValue] = useState("");
    const [count, setCount] = useState(0);
    const handleChange = (onKeyPress: any) => onKeyPress.target.value === letter ? setValue(onKeyPress.target.value) : setCount(count + 1);
    return (
        <div>
            Try this first Exercise: <br />
            <Input size="lg" type="text" placeholder={letter} w="3rem" value={value} onChange={handleChange} />
            <div onChange={handleChange}>{score} errors</div>
        </div>
    )
}
