import React, { useState } from "react";
import { Input } from "@chakra-ui/core";

export const Exercise = () => {
    const letter = "a";
    const [value, setValue] = useState("");
    const handleChange = (event: any) => event.target.value === letter ? setValue(event.target.value) : null;

    return (
        <div>
            Try this first Exercise: <br />
            <Input size="lg" type="text" placeholder={letter} w="3rem" value={value} onChange={handleChange} />
        </div>
    )
}
