import React from "react";
import {
  CSSReset,
  ChakraProvider as ChakraUiThemeProvider
} from "@chakra-ui/core";

import theme, { Theme } from "@chakra-ui/theme";

import emotionStyled, { CreateStyled } from "@emotion/styled";

export const styled = emotionStyled as CreateStyled<Theme>;

const fonts = {
  ...theme.fonts,
  heading: "'Poppins', sans-serif",
  body: "'Source Sans Pro', sans-serif",
  mono: "Menlo, monospace"
};

const colors = {
  ...theme.colors,
  primary: {
    50: "#E4FAFF",
    100: "#C2F5FF",
    200: "#91E9FF",
    300: "#61D9FF",
    400: "#31C5FF",
    500: "#02AEF2",
    600: "#0099D4",
    700: "#0080A8",
    800: "#00647D",
    900: "#004452",
    gradient: "linear-gradient(90deg, #02AEF2 0%, #5BDBD4 100%);"
  },
  secondary: {
    50: "#E5FDFD",
    100: "#CCFFFF",
    200: "#A9FFFB",
    300: "#8AF8F2",
    400: "#70ECE5",
    500: "#5BDBD4",
    600: "#41C0B9",
    700: "#2CA19A",
    800: "#1B7D78",
    900: "#052626"
  },
  neutral: {
    50: "#F8F8F8",
    100: "#E7E7E7",
    200: "#D3D3D3",
    300: "#C0C0C0",
    400: "#ACACAC",
    500: "#999999",
    600: "#7F7F7F",
    700: "#656565",
    800: "#4B4B4B",
    900: "#313131",
    1000: "#171717",
    gradient: "linear-gradient(90deg, #999999 0%, #DDDDDD 100%);"
  },
  contrast: {
    50: "#FBEDEB",
    100: "#FFCEC4",
    200: "#FFAC9C",
    300: "#FF907B",
    400: "#FF7760",
    500: "#FF644C",
    600: "#D44931",
    700: "#A8321C",
    800: "#7D1F0C",
    900: "#521003",
    1000: "#260600",
    gradient: "linear-gradient(90deg, #FF644C 0%, #FFAD4C 100%);"
  },
  contrastSecondary: {
    50: "#FFF9EB",
    100: "#FFEDC4",
    200: "#FFDE9C",
    300: "#FFCE7B",
    400: "#FFBE60",
    500: "#FFAD4C",
    600: "#D49131",
    700: "#A8741C",
    800: "#7D570C",
    900: "#523903",
    1000: "#261B00"
  },
  semantic: {
    valid: "#0DB518",
    error: "#E42929",
    warning: "#EFAB22",
    info: "#0085FF"
  }
};

type CustomTheme = Theme & {
  fonts: typeof fonts;
  colors: typeof colors;
};

export const customTheme: CustomTheme = {
  ...theme,
  fonts,
  colors
};

export const ThemeProvider = ({
  children
}: {
  children: JSX.Element;
}): JSX.Element => (
    <ChakraUiThemeProvider theme={customTheme}>
      <CSSReset />
      {children}
    </ChakraUiThemeProvider>
  );
