import React, { useState } from "react";
import { Input } from "@chakra-ui/core";
import { keys } from "../../__mocks__";

export const getRandomKey = () => { return keys[Math.floor(Math.random() * keys.length)] };

const SingleInput = ({ handleChange, keyValue }: { handleChange: any, keyValue: string }) => {
    // const [value, setValue] = useState(["", "", "", "", ""]);

    return (
        <Input name="" size="lg" type="text" placeholder={key} w="3rem" value={keyValue} onChange={handleChange} />
    )
}

export const Exercise = () => {
    const [keyValues, setKeyValue] = useState(["", "", "", "", ""]);
    const [error, setError] = useState(0);
    const [valid, setValid] = useState(0);
    const handleChange = (index: number) => (onKeyPress: any) => {
        if (onKeyPress.target.value === key) {
            setKeyValue((previousArray: string[]): string[] => {
                const newArray = [...previousArray];
                newArray[index] = onKeyPress.target.value;
                return newArray;
            });
            setValid(valid + 1)
        } else { setError(error + 1) }
    }
    return (
        <div>
            Try this first Exercise: <br />
            <SingleInput handleChange={handleChange(0)} keyValue={keyValues[0]} />
            <SingleInput handleChange={handleChange(1)} keyValue={keyValues[1]} />
            <SingleInput handleChange={handleChange(2)} keyValue={keyValues[2]} />
            <SingleInput handleChange={handleChange(3)} keyValue={keyValues[3]} />
            <div>{valid} valids | {error} errors</div>
        </div>
    )
}

